<?php
// 現在のページのパスを取得
$current_page = $_SERVER['REQUEST_URI'];

// 背景色を取得する関数
function get_bg_color($page) {
  if ($page === '/' || $page === '') {
    return 'bg-gray-300';
  }
  return 'bg-white';
}

// 現在のページの背景色を取得
$bg_color = get_bg_color($current_page);
?>

